mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID = #{userId}

SELECT COUNT(*)
FROM MEMBER
WHERE NICKNAME = #{nickName}

mapper.insert:
INSERT INTO MEMBER (
    USER_ID
    , USER_PWD
    , EMAIL
    , BIRTHDAY
    , GENDER
    , NICKNAME
    , FAVORITE_GENRE
    , PICTURE
)
VALUES (
    #{userId}
    , #{userPwd}
    , #{email}
    , #{birthday}
    , #{gender}
    , #{nickName}
    , #{favoriteGenre}
    , #{picture}
)
