mapper.select:
SELECT COUNT(*)
FROM MOVIE
WHERE MOVIE_ID = #{value}

mapper.insert:
INSERT INTO MOVIE(
      MOVIE_ID
    , MOVIE_TITLE
    , CATEGORY
    , DIRECTOR
    , ADULT
    , RELEASE_DATE
    , POPULARITY
    , RATING_SCORE
    , REVIEW_COUNT
    , LIKE_COUNT
    , DISLIKE_COUNT
    , CONTENT
)
VALUES (
      #{movieId}
    , #{movieTitle}
    , #{category}
    , #{director}
    , #{adult}
    , CASE
        WHEN #{releaseDateStr} IS NULL OR #{releaseDateStr} = ''
        THEN NULL
        ELSE TO_DATE(#{releaseDateStr}, 'YYYY-MM-DD')
      END
    , #{popularity}
    , #{ratingScore}
    , #{reviewCount}
    , #{likeCount}
    , #{dislikeCount}
    , #{content}
)

mapper.select:
SELECT LIKE_TYPE
FROM MOVIE_LIKE
WHERE MOVIE_ID = #{movieId}
  AND USER_ID  = #{userId}

mapper.update:
UPDATE MOVIE_LIKE
   SET LIKE_TYPE = #{likeType}
 WHERE MOVIE_ID = #{movieId}
   AND USER_ID  = #{userId}

mapper.delete:
DELETE FROM MOVIE_LIKE
 WHERE MOVIE_ID = #{movieId}
   AND USER_ID  = #{userId}
