mapper.update:
UPDATE REVIEW
SET VIEW_COUNT=VIEW_COUNT+1
WHERE REVIEW_ID=#{rno}

mapper.select:
	SELECT LIKE_YN
   FROM REVIEW_LIKE
   WHERE REVIEW_ID = #{reviewId}
      	  AND USER_ID = #{userId}

mapper.update:
	UPDATE REVIEW_LIKE
	SET LIKE_YN = DECODE(LIKE_YN, 'Y', 'N', 'Y')
   WHERE REVIEW_ID = #{reviewId}
        AND USER_ID = #{userId}

mapper.select:
SELECT COUNT(*) FROM REVIEW JOIN MEMBER M USING(USER_ID)
WHERE REVIEW_TITLE LIKE '%'||#{keyword}||'%'
WHERE NICKNAME = #{keyword}

SELECT COUNT(*)
FROM MOVIE
WHERE MOVIE_ID = #{movieId}

mapper.insert:
INSERT INTO MOVIE(
      MOVIE_ID
    , MOVIE_TITLE
    , CATEGORY
    , DIRECTOR
    , ADULT
    , RELEASE_DATE
    , POPULARITY
    , RATING_SCORE
    , REVIEW_COUNT
    , LIKE_COUNT
    , DISLIKE_COUNT
    , CONTENT
)
VALUES (
      #{movieId}
    , #{movieTitle}
    , #{category}
    , #{director}
    , #{adult}
    , CASE
        WHEN #{releaseDateStr} IS NULL OR #{releaseDateStr} = ''
        THEN NULL
        ELSE TO_DATE(#{releaseDateStr}, 'YYYY-MM-DD')
      END
    , #{popularity}
    , #{ratingScore}
    , #{reviewCount}
    , #{likeCount}
    , #{dislikeCount}
    , #{content}
)
